CavebotScript.script:
init start
filteruseroptions(true) -- don't disable this
local Monsters = {"Medusa", "Serpent Spawn", "Hydra", "Behemoth", "Souleater", "Eternal Guardian"}
local Players = { Consider = true, Distance = 10, FloorDifference = 1, SafeList = {"Bubble", "Eternal Oblivion"}, }
local Spells = { {Name = "exori min", Amount = 2}, }
local SpecialAreas = { -- {min x, max x, min y, max y, z} }
local UseTargetState = false
-- DO NOT CHANGE ANYTHING BELOW THIS LINE
local i, LastFloor, Exhaust = 1, $posz, $timems
while Spells[i] ~= nil do Spells[i].Info = spellinfo(Spells[i].Name)
if Spells[i].Info.words == 0 then table.remove(Spells, i) else Spells[i].Monsters = Spells[i].Monsters or Monsters Spells[i].NeedDirection = table.find({"WaveSmall", "WaveVerySmall", "WaveBig", "BeamSmall", "BeamBig", "Front", "Strike"}, Spells[i].Info.castarea) ~= nil Spells[i].AttackSupport = Spells[i].Info.group:match("Support") ~= nil
table.lower(Spells[i].Monsters) i = i + 1 end end
init end
auto(200, 400)
if $posz ~= LastFloor then LastFloor, Exhaust = $posz, $timems + 2000 return end
if maround(5, "Medusa", "Serpent Spawn", "Hydra", "Behemoth", "Souleater") <= 3 and $attacked.id ~= 0 and $mp > 400 then if $timems >= Exhaust and ($targeting or not UseTargetState) then for _, Spell in ipairs(Spells) do if cancast(Spell.Info) and not isinsidearea(SpecialAreas) then if Spell.Amount and (not Players.Consider or paroundfloorignore(Players.Distance, Players.FloorDifference, unpack(Players.SafeList)) == 0) then local BestAmount, BestDir = 0, $self.dir
if Spell.NeedDirection then for Dir, Amount in pairs({n = 0, e = 0, s = 0, w = 0}) do Amount = maroundspell(Spell.Name, Dir, unpack(Spell.Monsters))
if Amount > BestAmount or (Amount >= BestAmount and Dir == $self.dir) then BestAmount, BestDir = Amount, Dir end end else BestAmount = not Spell.AttackSupport and maroundspell(Spell.Name, BestDir, unpack(Spell.Monsters)) or maround(1, false, unpack(Spell.Monsters)) end
if BestAmount >= math.max(Spell.Amount, 1) then while $self.dir ~= BestDir do turn(BestDir) waitping() end cast(Spell.Name) waitping() end elseif Spell.Hppc and $attacked.hppc >= math.max(Spell.Hppc, 1) and table.find(Spell.Monsters, $attacked.name:lower()) and cancast(Spell.Info, $attacked) then cast(Spell.Name) waitping() end end end end end

Waypoint.script:
--Open all bps closewindows() setsetting('Looting/OpenNextBP', 'no')
local supplybp = getuseroption("SuppliesBP") local lootbp = getuseroption("LootBP") local goldbp = getuseroption("GoldBP") local miscbp = getuseroption("MiscBP") local extrasupplybp = getuseroption("ExtraSuppliesBP")
while windowcount() < 3 do closewindows() wait(1234,2345) openitem(0, "back", true) waitping(4,8) resizewindows() openitem(supplybp,0,true) waitping(4,8) resizewindows() openitem(lootbp,0,true) waitping(4,8) resizewindows() end
while getuseroption("LootGP") and itemcount(goldbp) > 0 and windowcount(goldbp) == 0 do openitem(goldbp,0,true) waitping(4,8) end resizewindows()
while getuseroption("UseExtraSupplyBp") and itemcount(extrasupplybp) > 0 and windowcount(extrasupplybp) == 0 do openitem(extrasupplybp,0,true) waitping(4,8) end resizewindows()
if (getuseroption("UseNecklaces") or getuseroption("UseRings")) and itemcount(miscbp) > 0 then while windowcount(miscbp) ~= 1 do openitem(miscbp,0,true) waitping(4,8) end end resizewindows()
--Walk through players? if getuseroption("WalkThruPlayer") then setsetting('Cavebot/WalkThroughPlayers', 'yes') else setsetting('Cavebot/WalkThroughPlayers', 'no') end

--Location check / OPEN BPS if not islocation(3) then if $standtime > 5000 then settargeting("yes") end gotolabel(0, "ReadyToHunt") else setsetting('Looting/OpenNextBP', 'no')
local supplybp = getuseroption("SuppliesBP") local lootbp = getuseroption("LootBP") local goldbp = getuseroption("GoldBP") local miscbp = getuseroption("MiscBP") local extrasupplybp = getuseroption("ExtraSuppliesBP")
while windowcount() < 3 do closewindows() wait(1234,2345) openitem(0, "back", true) waitping(4,8) resizewindows() openitem(supplybp,0,true) waitping(4,8) resizewindows() openitem(lootbp,0,true) waitping(4,8) resizewindows() end
while getuseroption("LootGP") and itemcount(goldbp) > 0 and windowcount(goldbp) == 0 do openitem(goldbp,0,true) waitping(4,8) end resizewindows()
while getuseroption("UseExtraSupplyBp") and itemcount(extrasupplybp) > 0 and windowcount(extrasupplybp) == 0 do openitem(extrasupplybp,0,true) waitping(4,8) end resizewindows()
if (getuseroption("UseNecklaces") or getuseroption("UseRings")) and itemcount(miscbp) > 0 then while windowcount(miscbp) ~= 1 do openitem(miscbp,0,true) waitping(4,8) end end resizewindows()
end

