--SETTINGS/Hunt Option/Open all bps setsetting('Looting/OpenNextBP', 'no') setlooting("yes")
local supplybp = getuseroption("SuppliesBP") local lootbp = getuseroption("LootBP") local goldbp = getuseroption("GoldBP") local miscbp = getuseroption("MiscBP") local extrasupplybp = getuseroption("ExtraSuppliesBP")
