while getuseroption("LootGP") and itemcount(goldbp) > 0 and windowcount(goldbp) == 0 do openitem(goldbp,0,true) waitping(4,8) end resizewindows()
while getuseroption("UseExtraSupplyBp") and itemcount(extrasupplybp) > 0 and windowcount(extrasupplybp) == 0 do openitem(extrasupplybp,0,true) waitping(4,8) end resizewindows()
while (getuseroption("UseNecklaces") or getuseroption("UseRings")) and itemcount(miscbp) > 0 and windowcount(miscbp) == 0 do openitem(miscbp,0,true) waitping(4,8) end resizewindows() setsetting('Looting/OpenNextBP', 'yes')
if getuseroption("HuntFloor3East") then gotolabel("Floor3East") elseif getuseroption("HuntFloor3West") then gotolabel("Floor3West") else gotolabel("Floor3Done") end
